@import url("https://fonts.googleapis.com/css2?family=DM+Serif+Display&family=Inter&display=swap");
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&display=swap');

#root {
  width: 100vw;
  height: 100vh;
}

body {
  margin: 0;
}

::-webkit-scrollbar {
  width: 2px;
}

/* TRACK */
::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.23);
}

/* HANDLE */
::-webkit-scrollbar-thumb {
  background: white;
  height: 10%;
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
}

.overlay--disable {
  pointer-events: none;
}

.loader {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: whitesmoke;
  transition: opacity 2s ease-in-out;
}

.loader--disappear {
  opacity: 0;
}

.intro {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 100%;
}

.logo {
  font-family: "DM Serif Display", serif;
  color: black;
  font-size: 4rem;
  letter-spacing: 0.4rem;
  padding: 0;
  margin: 0;
  width: 40%; /* Restrict the width to 40% of the screen */
  text-align: left; /* Align text to the left within the 40% width */
  position: absolute;
  top: 50vh;
  left: 0; /* Start from the left of the screen */
  animation: fadeUp 2.4s ease-in-out;
  transition: all 2s ease-in-out;
}
.intro--disappear .logo { top: 0; left: 50%; transform: translateX(-50%) scale(0.5); transform-origin: center top; }



.spinner {
  position: absolute;
  right: 0px;
  top: 0px;
  margin-right: -42px;
  margin-top: -42px;
  animation: fadeIn 2.4s ease-in-out 1.2s forwards;
  opacity: 0;
}

.spinner__image {
  width: 164px;
  height: 164px;
  background-size: contain;
  background-image: url("../public/images/wawa.svg");
  animation: rotate 10s linear infinite;
}

.intro--disappear .spinner {
  opacity: 1;
  animation: fadeOut 1s ease-in-out forwards;
}

.intro__scroll {
  opacity: 0;
  color: white;
  font-family: "Inter", sans-serif;
  font-size: 2rem;
  letter-spacing: 0.05rem;
  position: absolute;
  top: 24vh;
}

.intro--disappear .intro__scroll {
  animation: fadeIn 1s ease-in-out 1.5s forwards;
}

.overlay--scrolled .intro__scroll {
  animation: fadeOut 1s ease-in-out forwards;
}

.explore {
  padding: 1rem 2rem;
  border: none;
  font-family: "DM Serif Display", serif;
  font-size: 1.25rem;
  letter-spacing: 0.25rem;
  color: #3535cc;
  border-radius: 32px;
  cursor: pointer;
  display: inline-block;
  margin-top: 500px;
  position: relative;
  z-index: 10; /* Ensure the explore button is clickable */
  overflow: hidden;
  transition: 0.42s ease-in-out;
  animation: fadeIn 2.4s ease-in-out 2s forwards;
  opacity: 0;
}

.explore:hover {
  color: white;
}

.explore:hover:before {
  width: 200%;
  height: 300%;
  border-radius: 100%;
}

.explore:before {
  content: "";
  background: #3535cc;
  position: absolute;
  width: 0%;
  bottom: 0;
  left: 50%;
  height: 0%;
  transition: 0.42s ease-in-out;
  z-index: -1;
  transform: translateX(-50%) translateY(50%);
}

.intro--disappear .explore {
  animation: fadeOut 1s ease-in-out forwards;
}

.outro {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: opacity 4s ease-in-out;
}

.outro--appear {
  opacity: 1;
}

.outro__text {
  font-family: "Inter", sans-serif;
  font-size: 1.5rem;
  color: black;
}

.social-icons {
  display: flex;
  gap: 10px; /* Adjust spacing between icons */
  margin-top: 20px; /* Space above the social icons */
  z-index: 10; /* Ensure the icons are clickable */
}

.social-icon {
  width: 40px;
  height: 40px;
  cursor: pointer; /* Ensure the cursor shows a pointer */
}

.social-icon img {
  width: 100%;
  height: 100%;
  object-fit: contain; /* Ensure the icon scales correctly */
}

/* RESPONSIVE */

@media screen and (max-width: 768px) {
  .logo {
    font-size: 3rem;
    letter-spacing: 0.25rem;
  }

  .intro--disappear .logo {
    top: 24px;
    transform: scale(0.5);
    transform-origin: center top;
  }

  .spinner__image {
    width: 82px;
    height: 82px;
  }

  .outro__text {
    font-size: 1rem;
  }
}

/* ANIMATIONS */

@keyframes fadeUp {
  0% {
    opacity: 0;
    transform: translateY(100px);
    filter: blur(9px);
  }

  100% {
    opacity: 1;
    filter: none;
  }
}

@keyframes fadeIn {
  0% {
    opacity: 0;
  }

  100% {
    opacity: 1;
  }
}

@keyframes fadeOut {
  0% {
    opacity: 1;
  }

  100% {
    opacity: 0;
  }
}

@keyframes rotate {
  100% {
    transform: rotate(360deg);
  }
}

*{

    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    box-sizing: border-box;
    list-style: none;
    text-decoration: none;
}

/* defining some css variables */

:root{

    --color-bg: #0e0e0f;
    --color-bg-variant: #0c0c0e;
    --color-primary: #96600f;
    --color-primary-variant: rgba(77, 181, 255, 0.4);
    --color-white: #d6c7c7;
    --color-light: rgba(128, 131, 139, 0.6);

    --container-width-lg: 70%;
    --container-width-md: 86%;
    --container-width-sm: 90%;

    --transition: all 400ms ease;


}

html{

    scroll-behavior: smooth;
}

/* removing the scrollbar */

::-webkit-scrollbar{

    display: none;
}

body{

    font-family: 'Poppins', sans-serif;
    background: var(--color-bg);
    color: var(--color-white);
    line-height: 1.7;
}

/* General Styles */

.container{

    width: var(--container-width-lg);
    margin: 0 auto;  /*position in the middle horizontally */
}

h1, h2, h3, h4, h5{

    font-weight: 500;
}

h1{

    font-size: 2.5rem;
}

section{

    margin-top: 8rem; /*Having some white space around different section*/

}

section > h2, section > h5{

    text-align: center;
    color: var(--color-light);
}

section > h2{

    color: var(--color-primary);
    margin-bottom: 3rem;
}

.text-light{

    color: var(--color-light);
}

a{

    color: var(--color-primary);
    transition: var(--transition);
}

a:hover{

    color: var(--color-white);
}

.btn{

    width: max-content; /* fit exactly it's content */
    display: inline-block;
    color: var(--color-primary);
    padding: 0.75rem 1.2rem;
    border-radius: 0.4rem;
    cursor: pointer;
    border: 1px solid var(--color-primary);
    transition: var(--transition);
}

.btn:hover{

    background: var(--color-white);
    color: var(--color-bg);
    border-color: transparent;
}

.btn-primary{

    background: var(--color-primary);
    color: var(--color-bg);
}

img{

    display: block;
    width: 100%;
    object-fit: cover;
}

/* Media queries for medium devices(Tablets) */

@media screen and (max-width: 1024px) {

    .container{

        width: var(--container-width-md);
    }

    section{

        margin-top: 6rem;
    }

}

/* Media queries for small devices(Mobiles) */

@media screen and (max-width: 600px) {

    .container{

        width: var(--container-width-sm);
    }

    section > h2{

        margin-bottom: 2rem;
    }

}
